<?php

namespace Tests\Feature;

use Illuminate\Foundation\Testing\RefreshDatabase;
use Illuminate\Foundation\Testing\WithFaker;
use Tests\TestCase;
use App\Models\User;

class CardCreateTest extends TestCase
{
    use RefreshDatabase;
    /**
     * A basic feature test example.
     *
     * @return void
     */
    public function test_card_create_likeAsPlayer()
    {
       // $this->withoutExceptionHandling();
        //Crear usuario con rol especifico y obtener el token para estar autenticado al llamar al endpoint.
        $rol = 'player';
        $user = User::factory()->create();
        $token = $this->getToken($user, $rol);

        //Crear Body y Header
        $params = $this->buildParams($token);
        
        //Consumir el EndPoint con Body y Headers
        $response = $this->json('POST', '/api/cards', $params['body'], $params['headers']);

        $response->assertStatus(403)
                // ->dump() // Esto lo uso porque no sabemos que nombre la imgen tiene o el id de la categoria.
                ->assertJson([
                    "message" => "This action is unauthorized.",
                ]);
    }

    public function test_card_create_likeAsCardMakers()
    {
       // $this->withoutExceptionHandling();
        //Crear usuario con rol especifico y obtener el token para estar autenticado al llamar al endpoint.
        $rol = 'card_makers';
        $faker = \Faker\Factory::create();
        $user = User::factory()->create(['id' => $faker->numberBetween($min = 100, $max = 900)]);
        $token = $this->getToken($user, $rol);
        //Crear Body
        $params = $this->buildParams($token);
        
        //Consumir el EndPoint con Body y Headers
        $response = $this->json('POST', '/api/cards', $params['body'], $params['headers']);
        
        $response->assertStatus(200)
                // ->dump() // Esto lo uso porque no sabemos que nombre la imgen tiene o el id de la categoria.
                ->assertJson([
                    "status" => "Creado!",
                    "message" => "La carta se creo correctamente",
                ]);
    }

    private function buildParams($token){
        $faker = \Faker\Factory::create();
        $params['body'] = [
            "name" => 'Demmooooo',
            "sku_id" =>  $faker->numberBetween($min = 100, $max = 900),
            "first_edition" =>  $faker->numberBetween($min = 0, $max = 1),
            "serial_code" => 12340112,
            "category_code" => "003",
            "description" => "SARAZASKSKSKS",
            "price" => 1000,
            "card_creation_date" => "1996-02-20",
            "atk" => 1000,
            "def" => 2000,
            "qty_star" => 13,
            "image" => $this->base_64,
        ];

        $params['headers'] = [
            'Authorization' => 'Bearer ' . $token,
            'Content-Type' => 'application/json',
        ];
         
        return $params;
    }

    private $base_64 = "data:image/jpeg;base64,";
}
